package Session3;
import static java.lang.Math.*;
public class For {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		double squareRoot;
		for (i = 1; i <= 10; i++) {
		squareRoot = sqrt(i);
		System.out.println("the square root of " + i + " is " + (int)squareRoot);
		}
		System.out.println("That's All!");
	}

}
